transactions = {}

class User(UserMixin):
    def __init__(self, id, username, password):
        self.id = id
        self.username = username
        self.password = password

@login_manager.user_loader
def load_user(user_id):
    logger.debug(f"Loading user: {user_id}")
    user = users.get(user_id)
    if user:
        logger.debug(f"User {user_id} loaded successfully")
        return user
    else:
        logger.warning(f"User {user_id} not found")
        return None

@app.route('/')
@login_required
def index():
    logger.debug(f"Accessing index page. Current user: {current_user.username}")
    return render_template('index.html')

@app.route('/register', methods=['GET', 'POST'])
def register():
    logger.debug("Accessing register route")
    if request.method == 'POST':
        username = request.form['username']
        password = request.form['password']
        logger.debug(f"Attempting to register user: {username}")
        logger.debug(f"Current users before registration: {json.dumps(users, default=lambda o: o.__dict__, indent=2)}")
        if username in users:
            logger.warning(f"Registration failed: Username {username} already exists")
            flash('Username already exists')
            return redirect(url_for('register'))
        hashed_password = generate_password_hash(password)
        new_user = User(id=username, username=username, password=hashed_password)
        users[username] = new_user
        transactions[username] = []
        logger.info(f"New user registered: {username}")
        logger.debug(f"Current users after registration: {json.dumps(users, default=lambda o: o.__dict__, indent=2)}")
        flash('Registration successful. Please log in.')
        return redirect(url_for('login'))
    return render_template('register.html')

def is_safe_url(target):
    ref_url = urlparse(request.host_url)
    test_url = urlparse(urljoin(request.host_url, target))
    return test_url.scheme in ('http', 'https') and ref_url.netloc == test_url.netloc

@app.route('/login', methods=['GET', 'POST'])
def login():
    logger.debug("Accessing login route")
    if request.method == 'POST':
        username = request.form['username']
        password = request.form['password']
        logger.debug(f"Login attempt for user: {username}")
        logger.debug(f"Current users: {json.dumps(users, default=lambda o: o.__dict__, indent=2)}")
        user = users.get(username)
        if user and check_password_hash(user.password, password):
            login_user(user)
            logger.info(f"User {username} logged in successfully")
            next_page = request.args.get('next')
            logger.debug(f"Next page requested: {next_page}")
            if not next_page or not is_safe_url(next_page):
                next_page = url_for('index')
            logger.debug(f"Redirecting to: {next_page}")
            return redirect(next_page)
        else:
            logger.warning(f"Failed login attempt for user {username}")
            flash('Invalid username or password')
    return render_template('login.html')

@app.route('/logout')
@login_required
def logout():
    logger.debug(f"User {current_user.username} logged out")
    logout_user()
    return redirect(url_for('login'))

@app.route('/api/current_time')
def get_current_time():
    return jsonify({'current_time': datetime.now().strftime('%Y-%m-%d %H:%M:%S')})

@app.route('/api/transactions', methods=['GET', 'POST'])
@login_required
def handle_transactions():
    if request.method == 'GET':
        return jsonify(transactions.get(current_user.username, []))
    elif request.method == 'POST':
        data = request.json
        if data is None:
            return jsonify({'error': 'Invalid JSON data'}), 400
        transaction = {
            'id': len(transactions[current_user.username]),
            'amount': data.get('amount'),
            'description': data.get('description'),
            'type': data.get('type'),
            'category': data.get('category')
        }
        transactions[current_user.username].append(transaction)
        return jsonify(transaction), 201

@app.route('/api/transactions/<int:transaction_id>', methods=['DELETE'])
@login_required
def delete_transaction(transaction_id):
    user_transactions = transactions.get(current_user.username, [])
    for i, transaction in enumerate(user_transactions):
        if transaction['id'] == transaction_id:
            del user_transactions[i]
            return '', 204
    return jsonify({'error': 'Transaction not found'}), 404

@app.route('/api/categories')
@login_required
def get_categories():
    categories = set()
    for transaction in transactions.get(current_user.username, []):
        categories.add(transaction['category'])
    return jsonify(list(categories))

@app.route('/api/export/csv')
@login_required
def export_csv():
    user_transactions = transactions.get(current_user.username, [])

    # Create a StringIO object to write CSV data
    csv_output = io.StringIO()
    csv_writer = csv.writer(csv_output)

    # Write header
    csv_writer.writerow(['ID', 'Amount', 'Description', 'Type', 'Category'])

    # Write transactions
    for transaction in user_transactions:
        csv_writer.writerow([
            transaction['id'],
            transaction['amount'],
            transaction['description'],
            transaction['type'],
            transaction['category']
        ])

    # Create a response with CSV data
    output = csv_output.getvalue()
    csv_output.close()

    return send_file(
        io.BytesIO(output.encode()),
        mimetype='text/csv',
        as_attachment=True,
        download_name=f'transactions_{current_user.username}.csv'
    )

@app.route('/api/export/pdf')
@login_required
def export_pdf():
    user_transactions = transactions.get(current_user.username, [])

    # Create a BytesIO buffer for the PDF
    buffer = io.BytesIO()

    # Create the PDF object, using the BytesIO buffer as its "file."
    doc = SimpleDocTemplate(buffer, pagesize=letter)

    # Create the table data
    data = [['ID', 'Amount', 'Description', 'Type', 'Category']]
    for transaction in user_transactions:
        data.append([
            transaction['id'],
            transaction['amount'],
            transaction['description'],
            transaction['type'],
            transaction['category']
        ])

    # Create the table
    table = Table(data)

    # Add style to the table
    style = TableStyle([
        ('BACKGROUND', (0, 0), (-1, 0), colors.grey),
        ('TEXTCOLOR', (0, 0), (-1, 0), colors.whitesmoke),
        ('ALIGN', (0, 0), (-1, -1), 'CENTER'),
        ('FONTNAME', (0, 0), (-1, 0), 'Helvetica-Bold'),
        ('FONTSIZE', (0, 0), (-1, 0), 14),
        ('BOTTOMPADDING', (0, 0), (-1, 0), 12),
        ('BACKGROUND', (0, 1), (-1, -1), colors.beige),
        ('TEXTCOLOR', (0, 1), (-1, -1), colors.black),
        ('ALIGN', (0, 0), (-1, -1), 'CENTER'),
        ('FONTNAME', (0, 1), (-1, -1), 'Helvetica'),
        ('FONTSIZE', (0, 1), (-1, -1), 12),
        ('TOPPADDING', (0, 1), (-1, -1), 6),
        ('BOTTOMPADDING', (0, 1), (-1, -1), 6),
        ('GRID', (0, 0), (-1, -1), 1, colors.black)
    ])
    table.setStyle(style)

    # Add the table to the PDF
    elements = []
    elements.append(table)
    doc.build(elements)

    # Get the value of the BytesIO buffer and create the response
    pdf = buffer.getvalue()
    buffer.close()

    return send_file(
        io.BytesIO(pdf),
        mimetype='application/pdf',
        as_attachment=True,
        download_name=f'transactions_{current_user.username}.pdf'
    )

